const hamburger = document.querySelector(".hamburger"),
  body = document.querySelector("body"),
  navLink = document.querySelectorAll(".nav_link"),
  popUpMessage = document.querySelector("#pop-up_message"),
  popUpConteiner = document.querySelector("#pop-up_js"),
  popUp = document.querySelector(".pop-up"),
  clouse = document.querySelector(".clouse"),
  raitingItemsList = document.querySelectorAll(".raiting_item"),
  raitingItemsArray = Array.prototype.slice.call(raitingItemsList);
raitingItemsArray.forEach((e) =>
  e.addEventListener("click", () => {
    const { itemValue: t } = e.dataset;
    e.parentNode.dataset.totalValue = t;
  })
);
const clousePopUp = (e) => {
    e.preventDefault(),
      popUpConteiner.classList.remove("active"),
      body.removeAttribute("style", "overflow: hidden"),
      popUpConteiner.removeEventListener("click", clousePopUp);
  },
  openPopUpMessage = (e) => {
    e.preventDefault(),
      popUpConteiner.classList.contains("active")
        ? (popUpConteiner.classList.remove("active"),
          body.removeAttribute("style", "overflow: hidden"))
        : (popUpConteiner.classList.add("active"),
          body.setAttribute("style", "overflow: hidden"),
          popUpConteiner.addEventListener("click", (e) => {
            if ("pop-up_js" !== e.target.id) return !0;
            clousePopUp(e);
          }));
  },
  mobileMenu = (e) => {
    e.preventDefault();
    const t = document.querySelector(".hero_header_nav_list");
    hamburger.classList.toggle("active"),
      t.classList.toggle("active"),
      t.classList.contains("active")
        ? (body.setAttribute("style", "overflow: hidden"),
          navLink.forEach((e) => {
            e.addEventListener("click", function () {
              t.classList.remove("active"),
                hamburger.classList.remove("active"),
                body.removeAttribute("style", "overflow: hidden");
            });
          }))
        : body.removeAttribute("style", "overflow: hidden");
  };
  hamburger.addEventListener("click", mobileMenu),
  popUpMessage.addEventListener("click", openPopUpMessage),
  clouse.addEventListener("click", clousePopUp);
